def rbCity():    global sitelist, listradio    sitelist.clear()    for r in listradio:        r.destroy()    n = 0    for c1 in data["county"]:        if(c1 == city.get()):            site1 = data.iloc[n,0]            if(site1 not in sitelist):                sitelist.append(site1)        n+=1    sitemake()    rbSite()    def rbSite():    n = 0    for s in data.iloc[:,0]:        if(s == Site.get()):            pm = data.iloc[n,2]            if(pm=='' or pm=='ND'):                result1.set(s + "站的PM2.5值目前無資料！")            else:                    if(int(pm) <35):                        gradel = "低"                    elif(int(pm) <53):                        gradel = "中"                    elif(int(pm) <70):                        gradel = "高"                    else:                        gradel = "非常高"                    result1.set(s + "站的PM2.5值為" + str(pm) +                                gradel + "等級")            break        n +=1      def clickRefresh():    global data    data = pd.read_csv("/Users/liaobaihang/Downloads/null.csv")    rbSite()    def sitemake():    global sitelist, listradio    for c1 in sitelist:        rbtem = tk.Radiobutton(frame2, text=c1, variable=Site,                               value=c1, command=rbSite())        listradio.append(rbtem)        if(c1==sitelist[0]):            rbtem.select        rbtem.pack(side="left")        import tkinter as tkimport pandas as pddata = pd.read_csv("/Users/liaobaihang/Downloads/null.csv")win = tk.Tk()win.title("PM2.5")city = tk.StringVar()Site = tk.StringVar()result1 = tk.StringVar()citylist = []sitelist = []listradio = []#建立縣市串列for c1 in data["county"]:    if(c1 not in citylist):        citylist.append(c1)#建立第一個縣市的測站串列count = 0for c1 in data["county"]:    if(c1 == citylist[0]):        site1 = data.iloc[count,0]        if(site1 not in sitelist):            sitelist.append(site1)    count += 1    label1 = tk.Label(win, text = "縣市：", pady = 6,                  font=("新細明體", 12))label1.pack()frame1 = tk.Frame(win)frame1.pack()for i in range(0,3):    for j in range(0,8):        n = i * 8 + j         if(n < len(citylist)):            city1 = citylist[n]            rbtem = tk.Radiobutton(frame1, text = city1, variable=city,                                   value=city1, command=rbCity)            rbtem.grid(row=i,column=j)            if(n == 0):                rbtem.select()                labe2 = tk.Label(win, text="測站：", pady =6)labe2.pack()frame2 = tk.Frame(win)frame2.pack()sitemake()btnDown = tk.Button(win, text="更新資料", command=clickRefresh)btnDown.pack(pady=6)lalRe = tk.Label(win, textvariable=result1)lalRe.pack(pady=6)rbSite()win.mainloop()